    public get provider(): object | null | undefined
    {
        return this.provider_;
    }

    public getDriver(): Driver
    {
        return this.assignee_.provider as Driver;
    }

    public join(): Promise;
    public join(ms: number): Promise;
    public join(until: Date): Promise;

    public join(param?: number | Date): Promise
    {
        if (param === undefined)
            return this.joiners_.wait();
        else if (param instanceof Date)
            return this.joiners_.wait_until(param);
        else
            return this.joiners_.wait_for(param);
    }

    /* ----------------------------------------------------------------
        PROPERTIES
